package io.dataease.xpack.permissions.dataset.dto.ext.mapper;import com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.core.metadata.IPage;import io.dataease.api.permissions.dataset.dto.DataSetColumnPermissionsDTO;import io.dataease.api.permissions.dataset.dto.DataSetRowPermissionsTreeDTO;import org.apache.ibatis.annotations.*;@Mapperpublic interface ColumnPermissionsExtMapper extends BaseMapper<DataSetColumnPermissionsDTO> {    @Select(            """                         select *                     from per_dataset_column_permissions                    ${ew.customSqlSegment}                     """    )    @Results(            id = "columnPermissionsMap",            value = {                    @Result(property = "id", column = "id"),                    @Result(property = "enable", column = "enable"),                    @Result(property = "auth_target_type", column = "authTargetType"),                    @Result(property = "auth_target_id", column = "authTargetId"),                    @Result(property = "dataset_id", column = "datasetId"),                    @Result(property = "permissions", column = "permissions"),                    @Result(property = "white_list_user", column = "whiteListUser"),                    @Result(property = "update_time", column = "updateTime")            }    )    IPage<DataSetColumnPermissionsDTO> pager(IPage<DataSetColumnPermissionsDTO> page, @Param("ew") QueryWrapper queryWrapper);}